function insert()
{
	global $HTTP_SERVER_VARS, $HTTP_GET_VARS, $HTTP_POST_VARS, $HTTP_POST_FILES, $Translation;
	
	if(get_magic_quotes_gpc())
	{
		$d_from_p_id = $HTTP_POST_VARS["d_from_p_id"];
		$d_to_p_id = $HTTP_POST_VARS["d_to_p_id"];
		$d_message = $HTTP_POST_VARS["d_message"];
	}
	else
	{
		$d_from_p_id = addslashes($HTTP_POST_VARS["d_from_p_id"]);
		$d_to_p_id = addslashes($HTTP_POST_VARS["d_to_p_id"]);
		$d_message = addslashes($HTTP_POST_VARS["d_message"]);
	}
	
	sql("insert into Dialog (d_message) values (" . (($d_message != "") ? "'$d_message'" : "NULL") . ")");
	return mysql_insert_id();
}

function delete($selected_id, $AllowDeleteOfParents=false, $SkipChecks=false){
	// insure referential integrity ...
	global $Translation;


	sql("delete from Dialog where d_id='".addslashes($selected_id)."'");
}

function update($selected_id)
{
	global $HTTP_SERVER_VARS, $HTTP_GET_VARS, $HTTP_POST_VARS, $Translation;
	
	if(get_magic_quotes_gpc())
	{
		$d_from_p_id = $HTTP_POST_VARS["d_from_p_id"];
